import React, { cloneElement, useEffect, useRef, useState } from 'react';
import axios from "axios";
import serverURL from "../serverURL";
import './CalendarStyles.css'
import Button from "react-bootstrap/Button";
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';

import "../../node_modules/@syncfusion/ej2-base/styles/material.css";
import "../../node_modules/@syncfusion/ej2-buttons/styles/material.css";
import "../../node_modules/@syncfusion/ej2-calendars/styles/material.css";
import "../../node_modules/@syncfusion/ej2-dropdowns/styles/material.css";
import "../../node_modules/@syncfusion/ej2-inputs/styles/material.css";
import "../../node_modules/@syncfusion/ej2-lists/styles/material.css";
import "../../node_modules/@syncfusion/ej2-navigations/styles/material.css";
import "../../node_modules/@syncfusion/ej2-popups/styles/material.css";
import "../../node_modules/@syncfusion/ej2-splitbuttons/styles/material.css";
import "../../node_modules/@syncfusion/ej2-react-schedule/styles/material.css";
import { ScheduleComponent, Day, Week, WorkWeek, Month, Agenda, Inject } from '@syncfusion/ej2-react-schedule';
import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
import { DateTimePickerComponent } from '@syncfusion/ej2-react-calendars';
import { useLocation, useHistory } from "react-router-dom"

const EditScheduler = () => {
    const location = useLocation();
    const calendar = useRef();
    const [events, setEvents] = useState([]);
    const history = useHistory();
    const sendData = async () => {
        const events = calendar.current.eventsData;
        const group = location.state.group;
        const result = await axios.put("" + serverURL + "api/Event", {
            events: events,
            group: group
        });
        console.log(result);
        history.push({ pathname: "schedule/", state: { events: events, group: group } });

    }
    return (<div>
        <ButtonComponent id='add' title='Add' >Add</ButtonComponent>
        <ButtonComponent id='edit' title='Edit'>Edit</ButtonComponent>
        <ButtonComponent id='delete' title='Delete'>Delete</ButtonComponent> <ScheduleComponent ref={calendar} width='100%' height='550px' selectedDate={new Date(2018, 1, 15)} >
            <ViewsDirective>
                <ViewDirective option='Day' />
                <ViewDirective option='Week' />
                <ViewDirective option='WorkWeek' />
                <ViewDirective option='Month' />
            </ViewsDirective>
            <Inject services={[Day, Week, WorkWeek, Month]} />
        </ScheduleComponent>
    </div>);
    // return (<div><ButtonComponent onClick={() => sendData()} variant="link" >save schedule</ButtonComponent><ScheduleComponent ref={calendar}>
    //     <Inject services={[Day, Week, WorkWeek, Month, Agenda]} />
    // </ScheduleComponent></div>);

}
export default EditScheduler;
