import axios from 'axios';
import { useFormik } from 'formik';
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useLocation } from 'react-router-dom';
import serverURL from '../serverURL';
const CreateGroup = () => {
    const location = useLocation();
    const [isCreate, setIsCreate] = useState(false);
    const formik = useFormik({
        initialValues: {
            name: '',
            description: '',
        },
        onSubmit: values => {
            const result = axios.post(""+serverURL+"api/Group",{
                email:location.state.email,
                name:values.name,
                description:values.description
            })
            if(result.data === "ok")
            {
                setIsCreate(true);
            }
            else{
                alert(result.data);
            }
            
        },
    });
    //hear i need have the mail of manager
    return (<><h1>Welcome to create group</h1>
        <Form onSubmit={formik.handleSubmit}>
            <Form.Group>
                <Form.Label>Group name</Form.Label>
                <Form.Control type="text" placeholder="enter group name" id="name" name="name"
                    onChange={formik.handleChange} value={formik.values.name} />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>
            <Form.Group>
                <Form.Label>Description on your group</Form.Label>
                <Form.Control as="textarea" rows={3} id="description" name="description"
                    onChange={formik.handleChange} value={formik.values.description} />
            </Form.Group>
            <Button type="submit" variant="praimary">Submit</Button>
        </Form>
        <Button variant="outline-praimary" block>add volunteers to your group</Button>
        <Button variant="outline-praimary" block>edit schedule to your group</Button>
    </>
    )
}

export default CreateGroup;
