import { Link, useHistory } from 'react-router-dom';
import './login.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useFormik } from 'formik';
import axios from 'axios';
import React, { useState } from 'react';
import serverUrl from '../serverURL';

const SignIn = (props) => {
    const history = useHistory();
    const [isLogin, setIsLogin] = useState(false);

    const formik = useFormik({
        enableReinitialize: true,
        initialValues: {
            email: '',
            password: '',
            emailValid: ''
        },
        onSubmit: async (values) => {
            const email = values.email
            if (email === "") {
                formik.values.emailValid = "please type email";
            }
            else {

                formik.values.emailValid = "";
                const password = values.password;
                //get the group for this user
                const result = await axios.post("" + serverUrl + "/user/signIn", {
                    email: email,
                    password: password
                });
                console.log(result);
                // if (result.data.err) {
                //     history.push({ pathname: '/error', state: { status: result.data.status } });
                //     return;
                // }

                // if (result.data.user) {
                //     let user = result.data.user;
                //     if (user.email && user.password) {
                //         //paste hear
                //         console.log(result.data.user);
                //         user = JSON.stringify(user);
                //         localStorage.setItem("user", user);////save the user in the storage
                //         //go to choose group
                //         history.push("/");
                //     }
                //     if (user.email && !user.password) {
                //         user.password = password;
                //         history.push({ pathname: "/signup", state: user });

                //     }
                // }
                // else alert("your email or password is incorrect");
            }
        },
    });
    return (
        <div className="auth-wrapper">
            <div className="auth-inner">
                <form onSubmit={formik.handleSubmit}>
                    <button type="button" className="close" aria-label="Close" onClick={() => history.push("/")} >
                        <span aria-hidden="true" >&times;</span>
                    </button><br />
                    <h3>Sign In</h3>
                    <div className="form-group">
                        <label>Email</label>
                        <input type="email" id="email" name="email" className="form-control"
                            onChange={formik.handleChange} value={formik.values.email} />
                        <span id="emailValid" className="validMassage">{formik.values.emailValid}</span>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" id="password" name="password" className="form-control"
                            onChange={formik.handleChange} value={formik.values.password} />
                    </div><br />
                    <div className="form-group">
                        <button type="submit" className="btn btn-primary btn-block">Submit</button><br />
                        <Button variant="link" style={{ float: "right" }}> forget password</Button></div><br />
                    {isLogin ?
                        <Form.Group controlId="exampleForm.ControlSelect1">
                            <Form.Label>Example select</Form.Label>
                            <Form.Control as="select" >
                                <option>1</option>
                                <option>2</option>
                                <option>3</option>
                                <option>4</option>
                                <option>5</option>
                            </Form.Control>
                        </Form.Group> : ""}
                </form>
            </div><br />
            <div className="auth-inner">
                <h5>New in courseries - <Link to="/"> Create Account</Link></h5>
            </div>
        </div>
    );
}
export default SignIn;
