export interface Event {
    id: string;
    resourceId: string;
    start: string;
    end: string;
    title: string;
    bgColor: string;
    item: any;
    [key: string]: any;
}
export interface Task {
    id: string;
    title: string;
    name: string;
    bgColor: string;
    item: any;
    [key: string]: any;
}
export interface HeaderEvent {
    render: boolean;
    span: number;
    eventItem: Event;
}
export interface Resource {
    id: string;
    name: string;
    expanded?: boolean;
    parentId?: string;
    groupOnly?: boolean;
    indent?: number;
}
export interface MovedState {
    left: boolean;
    right: boolean;
    up: boolean;
    down: boolean;
}
export interface Header {
    time: string;
    nonWorkingTime: boolean;
}
export interface HeaderItem {
    time: string;
    nonWorkingTime: boolean;
    start: string;
    end: string;
    count: number;
    addMore: number;
    addMoreIndex: number;
    events: HeaderEvent[];
}
export interface ResourceEvent extends Resource {
    slotId: string;
    slotName: string;
    parentId?: string;
    groupOnly?: boolean;
    hasSummary: boolean;
    rowMaxCount: number;
    rowHeight: number;
    headerItems: HeaderItem[];
    indent: number;
    hasChildren: boolean;
    expanded: boolean;
    render: boolean;
    nonWorkingTime?: boolean;
    availableTimes?: TimeRange[];
}
export interface TimeRange {
    start: string;
    end: string;
}
